import sys
import math

s = str(input()).split()

def PigLatin(string):
    vowels = ['a', 'e', 'i', 'o', 'u']
    if string[0].lower() in vowels:
        return string + "yay"
    else:
        for i in range(len(string)):
            if string[i].lower() in vowels:
                return string[i:].lower() + string[:i].lower() + "ay"

answer = ""

for word in s:
    answer+=PigLatin(word)+" "

print(answer.strip().lower())
