import java.util.*;
import java.io.*;
import java.math.*;

class Solution {

    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);
        int W = in.nextInt();
        int H = in.nextInt();
        char[][] grid = new char[H][W];
        if (in.hasNextLine()) in.nextLine();
        for (int i = 0; i < H; i++) grid[i] = in.nextLine().toCharArray();

        int best = 0;
        for (int x = 0; x < W - 1; x++) {
            for (int y = 0; y < H - 1; y++) {
                if (grid[y][x] == '.' && grid[y+1][x] == '.' && grid[y][x+1] == '.' && grid[y+1][x+1] == '.') {
                    grid[y][x] = '#'; grid[y+1][x] = '#'; grid[y][x+1] = '#'; grid[y+1][x+1] = '#';
                    int score = 0;
                    score += testRow(grid, y);
                    score += testRow(grid, y+1);
                    score += testCol(grid, x);
                    score += testCol(grid, x+1);
                    if (score > best) best = score;
                    grid[y][x] = '.'; grid[y+1][x] = '.'; grid[y][x+1] = '.'; grid[y+1][x+1] = '.';
                }
            }
        }

        System.out.println(best);
    }

    private static int testRow(char[][] grid, int rowNum) {
        for (int x = 0; x < grid[rowNum].length; x++) {
            if (grid[rowNum][x] == '.') return 0;
        }
        return 1;
    }

    private static int testCol(char[][] grid, int colNum) {
        for (int y = 0; y < grid.length; y++) {
            if (grid[y][colNum] == '.') return 0;
        }
        return 1;
    }
}
