#include <iostream>
#include <string>

int main()
{
    std::string first_name;
    std::getline(std::cin, first_name);
    int first_health;
    std::cin >> first_health; std::cin.ignore();
    int first_attack;
    std::cin >> first_attack; std::cin.ignore();
    std::string second_name;
    std::getline(std::cin, second_name);
    int second_health;
    std::cin >> second_health; std::cin.ignore();
    int second_attack;
    std::cin >> second_attack; std::cin.ignore();

    int first_power = first_health + first_attack;
    int second_power = second_health + second_attack;
    if (first_power > second_power)
    {
        std::cout << first_name;
    }
    else if (first_power == second_power)
    {
        std::cout << "Draw";
    }
    else
    {
        std::cout << second_name;
    }
}
