from itertools import permutations
from math import factorial as f

dirs=[(-1,0),(0,1),(1,0),(0,-1)]

class mari():
  def __init__(self,c):
    self.u=list(map(list,c)) # le plateau
    self.p=[] # la porte et la zone d’ouverture
    self.g=None # l’engrenage posé au sol
    self.k=None # la clé
    self.ins=[] # les instructions
    self.z=None # en cas de zigzag
    for i,l in enumerate(c):
      for j,e in enumerate(l):
        if "0"<=e<="3":
          self.d=int(e)
          self.x,self.y=i,j
          self.u[i][j]="."
        elif e=="E":
          self.a=i,j
          self.u[i][j]="."
        elif e=="d":
          self.p.append([i,j,"#."])
          self.u[i][j]="#"
        elif e=="r":
          self.p.append([i,j,".#"])
          self.u[i][j]="."
        elif e=="W":
          self.p.append([i,j,"##"])
          self.u[i][j]="#"
        elif e=="G":
          self.g=(i,j)
          self.u[i][j]="."
        elif e=="k":
          self.k=(i,j)
          self.u[i][j]="."

  def p1(self):
    x=self.x+dirs[self.d][0]
    y=self.y+dirs[self.d][1]
    if not (0<=x<7 and 0<=y<5):
      raise Exception("Sortie de carte")
    if self.u[x][y]!=".":
      raise Exception("Sortie de trajet")
    if self.g==(x,y):
      gx=x+dirs[self.d][0]
      gy=y+dirs[self.d][1]
      if self.u[gx][gy]!=".":
        raise Exception("Impossible de pousser les murs")
      if not (0<=gx<7 and 0<=gy<5):
        raise Exception("Impossible de sortir le boulon")
      self.g=(gx,gy)
    self.x,self.y=x,y

  def p2(self):
    self.p1()
    self.p1()

  def tr(self):
    self.d+=1
    self.d%=4

  def tl(self):
    self.d-=1
    self.d%=4

  def zr(self):
    if self.z==None:
      self.z=0
    [self.tr,self.tl][self.z]()
    self.z=1-self.z

  def zl(self):
    if self.z==None:
      self.z=1
    [self.tr,self.tl][self.z]()
    self.z=1-self.z

  def nop(self):
    pass

  def uk(self):
    if self.k!="ok":
      raise Exception("Il faut la clé")
    x,y,e=self.p[0]
    if self.x==x and self.y==y:
      raise Exception("Écrasé")
    self.p[0][2]=e[::-1]
    self.u[x][y]=self.p[0][2][0]
    x,y,e=self.p[2]
    if self.x==x and self.y==y:
      raise Exception("Écrasé")
    self.p[2][2]=e[::-1]
    self.u[x][y]=self.p[2][2][0]

  def fin(self):
    return (self.x,self.y)==self.a and self.k in {None,"ok"}

  def __str__(self):
    v=list(map(list,self.u))
    v[self.x][self.y]="↑→↓←"[self.d]
    if self.g:
      v[self.g[0]][self.g[1]]="o"
    return "\n".join("".join(l) for l in v)

  def action(self,a):
    {"p1":self.p1,"p2":self.p2,
     "tr":self.tr,"tl":self.tl,
     "zr":self.zr,"zl":self.zl,
     "uk":self.uk,
     "nop":self.nop}[a]()

  def actions(self):
    if "nop" in self.ins and self.ins[-1]!="nop":
      raise Exception("nop doit etre à la fin")
    t={i*(i+1)//2 for i in range(1,len(self.ins))}
    for i,a in enumerate(self.ins):
      if a=="bk":
        return
      if a=="x2" and i<len(self.ins):
        a=self.ins[i+1]
      self.action(a)
    if self.k==(self.x,self.y):
      self.k="ok"

def bf(n,m):
  f=1
  p=1
  while f<=n:
    p+=1
    f*=p
  f//=p
  p-=1
  l=[]
  while p:
    r,n=divmod(n,f)
    l.append(r)
    f//=p
    p-=1
  return l[::-1]+[0]*(m-len(l))

def solution(u,ins):
  j=ins[0]==""

  l=len(ins)-1
  solutions=set()

  for p in permutations(ins[1:]):
    for n in range(f(l+1-j)):
      m=mari(u)
      o=[]
      b=bf(n,l)
      if j:
        b=[0]+b[:-1]
      else:
        m.ins=[ins[0]]
      for i,e in enumerate(b):
        m.ins.insert(e,p[i])
        o+=m.ins
        try:
          m.actions()
        except Exception as e:
          break
        if m.fin():
          solutions.add(" ".join(o))
  if solutions:
    if len(solutions)==1:
      for s in sorted(solutions):
        l=2-j
        s=s.split()
        while s:
            a,s=s[:l],s[l:]
            print(*a)
            l+=1
    else:
      print(len(solutions))

