var x = 0;
var o = 0;

for (let i = 0; i < 3; i++) {
    var inputs = readline().split(' ');
    for (let j = 0; j < 3; j++) {
        let cell = inputs[j];

        if (cell=="X") {
            x++;
        } else if (cell=="O") {
            o++;
        }
    }
}

if (!(x-o in [1,0])) {
    console.log("Invalid");
} else if (x==5 && o==4) {
    console.log("Game over");
} else if (x>o) {
    console.log("O");
} else {
    console.log("X");
}
