//This is my first clash, so please be gentle ! thx
/*No peeping at the solution, clashers! >.> i'm watching u! */

unaffordableString = "No, the house is not affordable.";

//the solution below is expanded more for readability.


//First Line -
var inputs = readline().split(' ');
const income = parseInt(inputs[0]);
const mR = parseFloat(inputs[1]);
const savings = parseInt(inputs[2]);

//Following Lines
const N = parseInt(readline());
for (let i = 0; i < N; i++) {
    var inputs = readline().split(' ');
    const price = parseInt(inputs[0]); //market cost of the house.

    if(price*.1 > savings) print(unaffordableString); else{ //If she can't afford the down payment, the house isn't affordable. No other factors are important until she has secured the down payment.

        loan = price-savings;                   //price after the payment on the house is made.
        if(loan < 0)loan = 0                    //If the loan is overpaid, it goes back to 0.
        const expenses = parseInt(inputs[1]);   //Monthly expenses of house
        adjustedIncome = income-expenses;       //accounting for expenses in income
        monthlyMR = loan*mR/12;                 //Mortgage rates are calculated annually, then charged monthly. So, dividing by 12 because 12 years in 1 month.


        if(adjustedIncome>=monthlyMR){ 
            //Ruby can afford the house.
            print(
                "Yes, the house is affordable with $"//Initial string
                +Math.floor(adjustedIncome-monthlyMR)
                +" to spare."
            )
        } else {
            //Ruby can not afford the house.
            print(unaffordableString)
        }
    }
}
