tone = input()
message = input().split()
for word_index in range(len(message)):
    word = message[word_index]
    vowels = [letter for letter in word if letter in "aeiou"]
    word = "".join([letter for letter in word if letter not in ",.?;!'"])
    if word[0].isupper() or word.endswith("ing") or len(word) not in [5,6,7,8] or len(vowels)/len(word) > 0.45:
        message[word_index] = word.lower()
    else:
        message[word_index] = "".join([letter for letter in word if letter not in "aeiou"]).lower()

if tone != "None": message.append(["lol", ":(", ":)", ":D"][["Funny", "Sad", "Happy", "Joyous"].index(tone)])
print(*message)
