import java.util.*;
class Solution {
    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);
        int h = in.nextInt();
        int n = in.nextInt();
        if (in.hasNextLine()) {
            in.nextLine();
        }

        char[][]g=new char[h][h];
        for(int r=0;r<h;r++){
            for(int c=0;c<h;c++){
                g[r][c]='-';
            }
        }

        for (int i = 0; i < n; i++) {
            String a=in.next();
            String b=in.next();
            char x=a.charAt(0);
            switch(b){
                case "+":
                    for(int j=0;j<h;j++){
                        g[j][h/2]=x;
                        g[h/2][j]=x;
                    }
                    break;
                case "X":
                    for(int j=0;j<h;j++){
                        g[j][j]=x;
                        g[j][h-1-j]=x;
                    }
                    break;
                case "[]":
                    for(int j=0;j<h;j++){
                        g[0][j]=x;
                        g[j][0]=x;
                        g[j][h-1]=x;
                        g[h-1][j]=x;
                    }
                    break;
                case "<>":
                    for(int j=0;j<=h/2;j++){
                        g[j][h/2-j]=x;
                        g[j][h/2+j]=x;
                        g[h-1-j][h/2-j]=x;
                        g[h-1-j][h/2+j]=x;
                    }
                    break;
            }
        }
        for (int i = 0; i < h; i++) {
            String z="";
            for(char j:g[i])z+=j;
            System.out.println(z);
        }
    }
}
