word = input().lower()
training_data = "".join(i for i in input() if i.isalpha() or i in" -").lower()

def count_words_following_word(word, text):
    word_count = {}
    words = text.split()
    for i in range(len(words) - 1):
        if words[i] == word:
            following_word = words[i + 1]

            if following_word in word_count:
                word_count[following_word] += 1
            else:
                word_count[following_word] = 1
    return word_count

