import java.util.*;
import java.io.*;
import java.math.*;

/**
 * Auto-generated code below aims at helping you parse
 * the standard input according to the problem statement.
 **/
class Solution {

    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);
        String fraction = in.nextLine();
        if(fraction.charAt(0) == '[') System.out.println(cf(fraction));
        else System.out.println(fr(fraction));
    }

    public static String fr(String s)
    {
        String ans = "[";
        int[] frac = Arrays.stream(s.split("/")).mapToInt(Integer::parseInt).toArray();
        while(frac[1] > 0)
        {
            if(frac[0] < 0) ans += ((frac[0] / frac[1]) - 1) +"; ";
            else ans += (frac[0] / frac[1]) +(ans.length() == 1 ? "; " : ", ");
            int t = frac[0] < 0 ? (frac[0] % frac[1])+frac[1] : frac[0] % frac[1];
            frac[0] = frac[1];
            frac[1] = t;
        }
        return ans.substring(0, ans.length()-2)+"]";
    }

    public static String cf(String s)
    {
        String[] temp = s.substring(1, s.length() - 1).replaceAll(" ","").split("[^0-9^-]");
        int[] digits = new int[temp.length];
        for(int i = 0; i < temp.length; i++) digits[i] = Integer.parseInt(temp[i]);
        int p = 1;
        int q = digits[digits.length-1];
        for(int i = digits.length-2; i >= 0; i--)
        {
            int n = digits[i]*q + p;
            p = q;
            q = n;   
        }
        return q+"/"+p;
    }
}
