using System;
using System.Linq;
using System.IO;
using System.Text;
using System.Collections;
using System.Collections.Generic;

class Solution
{
    static void Main(string[] args)
    {
        string iPaddr = Console.ReadLine();
         try
        {
            string result = ConvertIpToHex(iPaddr);
            Console.WriteLine(result);
        }
        catch (Exception ex)
        {
            Console.WriteLine("Invalid IP address format!");
        }

    }
    public static string ConvertIpToHex(string ipAddress)
    {
        string[] segments = ipAddress.Split('.');

        // Check if there are exactly 4 segments
        if (segments.Length != 4)
        {
            throw new Exception("Invalid IP address format!");
        }

        byte[] bytes = new byte[4];

        for (int i = 0; i < 4; i++)
        {
            if (!int.TryParse(segments[i], out int value) || value < 0 || value > 255)
            {
                throw new Exception("Invalid IP address format!");
            }
            bytes[i] = (byte)value;
        }

        return $"{{0x{bytes[0]:X2}, 0x{bytes[1]:X2}, 0x{bytes[2]:X2}, 0x{bytes[3]:X2}}}";
    }
}
