 }

    for i := 0; i < 6; i++ {
        var LINE string
        fmt.Scan(&LINE)
        ln := []rune(LINE)
        fmt.Fprintln(os.Stderr, ln)
        board[i] = ln
    }

    if board[0][5] == '/' && board[1][4] == '/' && board[2][3] == '/' && board[3][2] == '/' && board[4][1] == '/' && board[5][0] == '/' {
        fmt.Println("NE-SW Diagonal")// Write answer to stdout
   } else if board[0][0] == '\\' && board[1][1] == '\\' && board[2][2] == '\\' && board[3][3] == '\\' && board[4][4] == '\\' && board[5][5] == '\\'{
        fmt.Println("NW-SE Diagonal")// Write answer to stdout
    } else {
        found := false
        for i := range(board) {
            if board[0][i] == '|' && board[1][i] == '|' && board[2][i] == '|' && board[3][i] == '|' && board[4][i] == '|' && board[5][i] == '|' {
                found = true
                fmt.Println("Vertical")// Write answer to stdout
                break
            }
        } 

        if !found {
			// i could just print out here, but just to be safe...
            for i := range(board) {
                if board[i][0] == '-' && board[i][1] == '-' && board[i][2] == '-' && board[i][3] == '-' && board[i][4] == '-' && board[i][5] == '-' {
                    found = true
                    fmt.Println("Horizontal")// Write answer to stdout
                    break
                }
            } 
        }
    }

    // fmt.Fprintln(os.Stderr, "Debug messages...")
