import java.text.DecimalFormat;
import java.util.Arrays;
import java.util.Scanner;

public class Solution {

    public static final DecimalFormat DF_2 = new DecimalFormat("0.00");

    public static void main(String[] args) {
        Scanner in = new Scanner(System.in);
        String entry = in.nextLine();
        double[] percentages = getPercentage(getHistogram(entry));

        draw(percentages);
    }

    public static int[] getHistogram(String entry) {
        int[] count = new int['Z'-'A'+1];
        for (char c : entry.toCharArray()) {
            c = Character.toUpperCase(c);
            if (c >= 'A' && c <= 'Z') {
                count[c-'A']++;
            }
        }
        return count;
    }

    public static double[] getPercentage(int[] counts) {
        double total = Arrays.stream(counts).sum();
        double[] percentages = new double[counts.length];
        for (int i=0; i < counts.length; ++i) {
            percentages[i] = counts[i] * 100 / total;
        }
        return percentages;
    }

    public static void draw(double[] percentages) {
        int i = 1;
        drawChar('A', percentages[0], Math.round(percentages[0]), Math.round(percentages[0]));
        for (char c = 'B'; c <= 'Y'; ++c, ++i) {
            drawChar(c, percentages[i], Math.round(Math.max(percentages[i], percentages[i-1])), Math.round(percentages[i-1]));
        }
        drawChar('Z', percentages[25], Math.round(Math.max(percentages[25], percentages[24])), Math.round(percentages[24]));
        drawLastLine(percentages[25]);
    }

    public static void drawChar(char c, double value, long top, long old) {
        StringBuilder sb = new StringBuilder();
        long rounded = Math.round(value);
        sb.append("  +");
        for (int j = 0; j < top; ++j) {
            if (j > 0 && (j == old || j==rounded)) {
                sb.append('+');
            } else {
                sb.append('-');
            }
        }
        if (top != 0 && (old >= top || old < rounded)) {
            sb.append("+");
        }
        sb.append("\n" + c + " " + (rounded == 0 ? "": "|"));
        for (int j = 0; j < rounded; ++j) {
            sb.append(' ');
        }
        sb.append('|' + DF_2.format(value) + "%");
        System.out.println(sb.toString());
    }

    public static void drawLastLine(double value) {
        long rounded = Math.round(value);
        System.out.print("  " + (rounded == 0 ? "" : "+"));
        for (int j = 0; j < rounded; ++j) {
            System.out.print('-');
        }
        System.out.print("+\n");
    }
}
