def count_unique_chars(word):
    return len(set(word))

def sort_strings_by_unique_chars(strings):
    return sorted(strings, key=count_unique_chars, reverse=True)

if __name__ == "__main__":
    input_string = input()
    strings = input_string.split()
    sorted_strings = sort_strings_by_unique_chars(strings)
    print(" ".join(sorted_strings))


