import java.io.IOException;
import java.util.Arrays;
import java.util.HashMap;
import java.util.Map;
import java.util.Scanner;
import java.util.Set;
import java.util.TreeSet;

public class Solution {

	public static void main(String[] args) throws IOException {
		Scanner in = new Scanner(System.in);

		String line1 = in.nextLine();
		if (!line1.matches("\\d+ \\d+")) {
			throw new IOException("width/height line contains illegal characters: " + line1);
		}
		String[] ss = line1.split("\\s");
		int w = Integer.parseInt(ss[0]);
		int h = Integer.parseInt(ss[1]);

		if (h > 20) {
			throw new IOException("haystack height is taller than 20: " + h);
		}

		StringBuilder sb = new StringBuilder();
		for (int y = 0; y < h; y++) {
			String s = in.nextLine();
			if (!s.matches("[0-9a-z]+")) {
				throw new IOException("haystack contains invalid characters: " + s);
			}
			if (s.length() != w) {
				throw new IOException("haystack width does not equal to w: " + s);
			}
			if (s.length() > 500) {
				throw new IOException("haystack line is longer than 500: " + s);
			}
			sb.append(s);
		}

		int n = in.nextInt();
		if (n > 20) {
			throw new IOException("too many test cases : " + n);
		}
		in.nextLine();
		for (int i = 0; i < n; i++) {
			String s = in.nextLine();
			if (!s.matches("[0-9a-z-,]+")) {
				throw new IOException("element definition contains invalid characters: " + s);
			}

			String haystack = sb.toString();
			Set<String> set = parseElementStr(s);
			for (String s1 : set) {
				int index = haystack.indexOf(s1.charAt(0));
				if (index < 0) {
					throw new IOException("wanted element is not found in haystack: " + s1);
				}
			}

			process(haystack, set, w, h);
		}
		in.close();
	}

	private static Set<String> parseElementStr(String wanted) throws IOException {
		Set<String> set = new TreeSet<>();
		String[] ss = wanted.split(",");
		for (String s1 : ss) {
			String[] ss1 = s1.split("-");
			if (ss1.length == 2) {
				if (!s1.matches("[\\da-z]-[\\da-z]")) {
					throw new IOException("formatting error in wanted element string: " + s1);
				}
				char st = ss1[0].charAt(0);
				char ed = ss1[1].charAt(0);
				for (int i = 0; i < ed - st + 1; i++) {
					set.add(String.valueOf((char) (i + st)));
				}
			} else if (ss1.length == 1) {
				if (!s1.matches("[\\da-z]+")) {
					throw new IOException("formatting error in wanted element string: " + s1);
				}
				set.add(ss1[0]);
			} else {
				throw new IOException("formatting error in wanted element string: " + s1);
			}
		}
		return set;
	}

	private static void process(String haystack, Set<String> set, int w, int h) {
		Map<String, Integer> map = new HashMap<>();
		int strLen = haystack.length();

		int ind = 0;
		int[] positions = new int[strLen];
		Arrays.fill(positions, -1);

		for (int i = 0; i < strLen; i++) {
			String s = String.valueOf(haystack.charAt(i));
			if (!set.contains(s)) {
				continue;
			}

			Integer v = map.get(s);

			if (v == null) {
				v = ind++;
				positions[i] = v;
				map.put(s, v);
			}

			else {
				positions[i] = v;
			}
		}

		// =====================================================
		int elementLen = set.size();
		int[] elementCount = new int[elementLen];
		int minp = 0, minq = strLen;
		int uniqueCount = 0;

		for (int p = 0, q = 0; p < strLen && q <= strLen;) {
			if (uniqueCount == elementLen) {
				if (q - p < minq - minp) {
					minp = p;
					minq = q;
				}
				if (positions[p] >= 0) {
					if (elementCount[positions[p]] == 1) {
						uniqueCount--;
					}
					elementCount[positions[p]]--;
				}
				p++;
			}

			else {
				if (q < strLen) {
					if (positions[q] >= 0) {
						if (elementCount[positions[q]] == 0) {
							uniqueCount++;
						}
						elementCount[positions[q]]++;
					}
					q++;
				}

				else {
					p++;
				}
			}
		}

//		String range = str.substring(minp, minq);
//		System.err.printf("%d %d %s %s\n", minp, minq - 1, set, range);

		System.out.printf("%d %d\n", minp, minq - 1);
	}
}
