fn main() {
    let mut input_line = String::new();
    io::stdin().read_line(&mut input_line).unwrap();
    let grades = input_line.trim_matches('\n').to_string();
    let mut input_line = String::new();
    io::stdin().read_line(&mut input_line).unwrap();
    let requirements = input_line.trim_matches('\n').to_string();

    let floats = grades
        .split_whitespace()
        .map(|s| s.parse::<f32>().unwrap())
        .collect::<Vec<f32>>();

    let requirements = requirements
        .split_whitespace()
        .collect::<Vec<&str>>();

    let average = || -> f32 {
        floats.iter().sum::<f32>() / floats.len() as f32
    };

    let median = || -> f32 {
        let mut floats = floats.clone();
        floats.sort_by(|a, b| a.partial_cmp(b).unwrap());

        let len = floats.len();
        if len % 2 == 0 {
            let mid = len / 2;
            (floats[mid - 1] + floats[mid]) / 2.0
        } else {
            floats[len / 2]
        }
    };

    let range = || -> f32 {
        let  max = floats.clone().into_iter().reduce(f32::max)
                .unwrap();
        let min = floats.clone().into_iter().reduce(f32::min)
                .unwrap();
        max - min
    };

    println!("{}", requirements.into_iter().map(|x| {
        match x {
            "median" => format!("{:.2}", median()),
            "range" => format!("{:.2}", range()),
            "average" => format!("{:.2}", average()),
            _ => unreachable!()
        }
    }).join(" "));
}
