#include <stdlib.h>
#include <stdio.h>
#include <string.h>
#include <stdbool.h>

int main() {

    int h;
    scanf("%d", &h);
    int m;
    scanf("%d", &m);
    int flight_time;
    scanf("%d", &flight_time); fgetc(stdin);
    char dep_tz[11];
    scanf("%[^\n]", dep_tz); fgetc(stdin);
    char arr_tz[11];
    scanf("%[^\n]", arr_tz);

    m = m + flight_time;
    h = h + m / 60;
    m = m % 60;
    int tomorrow = 0;

    int dep= dep_tz[4] -'0';
    int arr = arr_tz[4] -'0';

    if(dep_tz[3] == '-') {
        dep = - dep;
    }
    if(arr_tz[3] == '-') {
        arr = - arr;
    }

    h = h + (arr - dep);

    if(h >= 24) {
       h = h - 24;
        tomorrow = 1;
    }

    if(h < 10) {
     if(m < 10) {
            printf("0%d:0%d\n",h,m);
        }else printf("0%d:%d\n",h,m);
    }else {
        if(m < 10) {
            printf("%d:0%d\n",h,m);
            }
            else printf("%d:%d\n",h,m);
    }
    if(tomorrow == 1) {
        printf("TOMORROW");
    }
    return 0;
}
