import java.util.*;
import java.io.*;
import java.math.*;

/**
 * Auto-generated code below aims at helping you parse
 * the standard input according to the problem statement.
 **/
class Solution {

    public static void main(String args[]) {
        Scanner in = new Scanner(System.in); // initializing Scanner
        String STRING = in.nextLine(); // inputting String

        // Write an action using System.out.println()
        // To debug: System.err.println("Debug messages...");

        System.out.println(encode(STRING)); // displaying encoded string
    } // end of main
    
    public static String encode(String s) { // method to encode string
        String result = ""; // intitializing result
        for (int i = 0; i < s.length(); i++) {
            int add = (i+1)*(i+2)/2; // member of n(n+1)/2
            int index = (add+i)%s.length(); // calculating index
            result = result + s.charAt(index); // updating result
        } // end of loop(i)
        return result; // return result
    } // end of encode
} // end of class
