import java.util.*;

class Solution {
    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);
        int N = in.nextInt();
        if (in.hasNextLine()) {
            in.nextLine();
        }
        String ips = in.nextLine();
        String[] array = ips.split(" ");
        String results = "";
        for(String ip : array){
            String[] n = ip.split("\\."); 
            if(n.length != 4) continue;
            boolean isAIP = true;
            for(String s : n){
                try {
                int number = Integer.parseInt(s);
                if(number <= 255 && number >= 0) continue;
                isAIP = false;
                break;
            } catch(NumberFormatException e){
                isAIP = false;
                break;
            }
            } 
            if(isAIP)  results += ip +" ";
        }
        if(results.isEmpty()) {
            System.out.println("None"); 
        } else{
             System.out.println(results.substring(0,results.length()-1));
        }  
    }
}
