const [V, C, S] = readline().split(' ');

const Stock = {
    Vanilla: parseInt(V),
    Chocolate: parseInt(C),
    Strawberry: parseInt(S)
}

const N = parseInt(readline());

for (let i = 0; i < N; i++) {
    const order = readline().split(' ');

    order.forEach(flavour => {
        Stock[flavour]--;
    });
}

console.log(Object.keys(Stock).filter(flavour => Stock[flavour] < 0).join(' ') || 'Hired');
