program Answer;
{$H+}
{$mode Delphi}

uses
  sysutils, classes, math, types;

procedure ParseIn(Inputs: TStrings) ;
var Line : string;
begin
  readln(Line);
  Inputs.Clear;
  Inputs.Delimiter := ' ';
  Inputs.DelimitedText := Line;
end;

var
  i, dogMeetCount, diameter, windingcount, distance : Int32;
  Inputs : TStringList;
begin
  Inputs := TStringList.Create;
  dogMeetCount:=0;
  for i := 0 to 2 do
  begin
    ParseIn(Inputs);
    diameter := StrToInt(Inputs[0]);
    windingcount := StrToInt(Inputs[1]);
    distance := StrToInt(Inputs[2]);
    if diameter*Pi*windingcount >= distance*100 then
      inc(dogMeetCount);
  end;

  case dogMeetCount of
    0,1 : WriteLn('Frustration');
    2   : WriteLn('Little game');
    3   : WriteLn('Big game');
  end;

  flush(StdErr); flush(output); // DO NOT REMOVE
end.
