import java.util.*;

class Solution {

    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);
        int N = in.nextInt();
        int M = in.nextInt();
        List<Integer> invoices = new ArrayList<>();
        List<Integer> statements = new ArrayList<>();
        for (int i = 0; i < N; i++) {
            int invoice = in.nextInt();
            invoices.add(invoice);
        }
        for (int i = 0; i < M; i++) {
            int statement = in.nextInt();
            statements.add(statement);
        }
        lettrage(invoices, statements);
    }

    public static void lettrage(List<Integer> montantsFactures, List<Integer> montantsVirements) {
        char lettre = 'A';
        for (int virement : montantsVirements) {
            System.out.print(lettre + " " + virement + " - ");

            List<List<Integer>> combinations = findCombinationsHelper(montantsFactures, virement, 0, new ArrayList<>(), new ArrayList<>());
            if (!combinations.isEmpty()) {
                List<Integer> firstCombination = combinations.get(0);
                for (int i = 0; i < firstCombination.size(); i++) {
                    int facture = firstCombination.get(i);
                    System.out.print(facture + (i+1 == firstCombination.size() ? "" : " "));
                    montantsFactures.remove(Integer.valueOf(facture));
                }
            }
            System.out.println();
            lettre++;
        }
    }

    public static List<List<Integer>> findCombinationsHelper(List<Integer> montantsFactures, int target, int start, List<Integer> combination, List<List<Integer>> result) {
        if (target == 0) {
            result.add(new ArrayList<>(combination));
            return result;
        }
        for (int i = start; i < montantsFactures.size(); i++) {
            if (montantsFactures.get(i) > target) {
                continue;
            }
            combination.add(montantsFactures.get(i));
            result = findCombinationsHelper(montantsFactures, target - montantsFactures.get(i), i + 1, combination, result);
            combination.remove(combination.size() - 1);
        }
        return result;
    }
}
