import java.util.Scanner;

public class Solution {

	public static void main(String[] args) {
		Scanner in = new Scanner(System.in);
		int[] arr = new int[10];
		for (int j = 0; j < 10; j++) {
			arr[j] = in.nextInt();
		}
		process(arr);
	}

	private static void process(int[] arr) {
		int k = 1000; // individual weight limit
		int kk = 2001;
		int[] cc = new int[kk];
		int[] ccc = new int[kk]; // backup

		int sum = 0;
		for (int i = 0; i < 10; i++) {
			sum += arr[i];
			cc[arr[i]]++;
		}
		System.arraycopy(cc, 0, ccc, 0, kk); // create backup

		sum /= 4;

		for (int a = 1; a <= k; a++) {
			// restore cc
			System.arraycopy(ccc, 0, cc, 0, kk);

			for (int b = 1; b <= k; b++) {
				int ab = a + b;

				if (cc[ab] <= 0) {
					continue;
				} else {
					cc[ab]--;
				}

				for (int c = 1; c <= k; c++) {
					int ac = a + c;
					int bc = b + c;

					if (cc[ac] <= 0) {
						continue;
					} else {
						cc[ac]--;
					}
					if (cc[bc] <= 0) {
						continue;
					} else {
						cc[bc]--;
					}

					for (int d = 1; d <= k; d++) {
						int ad = a + d;
						int bd = b + d;
						int cd = c + d;

						if (cc[ad] <= 0) {
							continue;
						} else {
							cc[ad]--;
						}
						if (cc[bd] <= 0) {
							continue;
						} else {
							cc[bd]--;
						}
						if (cc[cd] <= 0) {
							continue;
						} else {
							cc[cd]--;
						}

						for (int e = 1; e <= k; e++) {
							int ae = a + e;
							int be = b + e;
							int ce = c + e;
							int de = d + e;
							if (cc[ae] <= 0) {
								continue;
							} else {
								cc[ae]--;
							}
							if (cc[be] <= 0) {
								continue;
							} else {
								cc[be]--;
							}
							if (cc[ce] <= 0) {
								continue;
							} else {
								cc[ce]--;
							}
							if (cc[de] <= 0) {
								continue;
							} else {
								cc[de]--;
							}

							if (a + b + c + d + e == sum) {
								System.out.printf("%d %d %d %d %d\n", a, b, c, d, e);
								return;
							}
						} // for e
					} // for d
				} // for c
			} // for b
		} // for a
	} // process

}
