n = int(input())
days = [*map(int,input().split())]

roman_numerals = '0 I II III IV V VI VII VIII IX X XI XII XIII XIV XV XVI XVII XVIII XIX'.split()
special_days = {1:'Kal Iul', 7:'Non Iul', 15:'Eid Iul', 32:'Kal Aug'}

def latin(d):
    special_day_number = next(s for s in (1,7,15,32) if s >= d)
    k = special_day_number - d
    special_day = special_days[special_day_number]
    if k == 0:
        delay = ''
    elif k == 1:
        delay = 'prid '
    else:
        roman = roman_numerals[k+1]
        delay = f'a d {roman} '
    return delay + special_day

for d in days:
    print(latin(d))
