{-# LANGUAGE TypeApplications #-}
import Data.Bits (setBit)
import Data.Char (intToDigit)
import Numeric (showIntAtBase)
fs = 1 : zipWith (+) fs (1:fs)
zs 0 = []
zs n = (length fs' - 1) : zs (n - last fs') where fs' = takeWhile (<= n) fs
main = interact $ flip (showIntAtBase 8 intToDigit) ""  . foldl (setBit @ Integer) 0 . zs . read
