import java.util.*;
class Solution {
    public static void main(String[] args) {
        //System.out.println("ENTER:");
        Scanner in = new Scanner(System.in);
        String element[] = new String[2];
        int valency[] = new int[2];
        for (int i = 0; i < 2; i++) {
            element[i] = in.next();
            valency[i] = in.nextInt();
        }
        //if (valency[0]>0 && valency[1]>0) {System.out.println("IMPOSSIBLE"); return;}
        //else if (valency[0]<0 && valency[1]<0) {System.out.println("IMPOSSIBLE"); return;}
        //else if (valency[0]==0 || valency[1]==0) {System.out.println("IMPOSSIBLE"); return;}
        if (valency[0]*valency[1]>=0) {System.out.println("IMPOSSIBLE");return;}
        int common = getHCF(Math.abs(valency[0]),Math.abs(valency[1]));
        valency[0] /= common;
        valency[1] /= common;
        String ans = element[0];
        if (Math.abs(valency[1])>1) ans+=""+Math.abs(valency[1]);
        ans += element[1];
        if (Math.abs(valency[0])>1) ans+=""+Math.abs(valency[0]);
        System.out.println(ans);
    }
    
    public static int getHCF(int A, int B) {
        int HCF = 1;
        for (int i = 1;i <= Math.min(A,B); i++) {
            if (A%i==0 && B%i==0) HCF=i;
        }
        return HCF;
    }
}
