import sys
import math

# Auto-generated code below aims at helping you parse
# the standard input according to the problem statement.

n = int(input())
rel = {}
for i in range(n):
    relation = input()

    parent, children = relation.split(" => ")
    children = children.split(",")

    for child in children:
        rel[child] = parent

p = input()
if p in rel.keys() or p in rel.values():
    ancestor = p
    while ancestor in rel:
        ancestor = rel[ancestor]
    if ancestor == p:
        print("No ancestors")
    else:
        print(ancestor,"is the most distant ancestor of",p)
else:
    print(p,"not found in the tree")
