function getDistance(x1, y1, x2, y2) {
	xDelta = x1 - x2;
	yDelta = y1 - y2;
	return Math.sqrt(Math.pow(xDelta, 2) + Math.pow(yDelta, 2));
}

var totalLength = 0.0;
var N = parseInt(readline());
var points = [];

for(var i = 0; i < N; i++) {
	var input = readline().split(' ');
    var x = parseInt(input[0]);
    var y = parseInt(input[1]);
	points.push(x + " " + y);
}

for(var i = 0; i < points.length - 1; i++) {
	var nearest = 0;
	var smallestDistance = Number.MAX_VALUE;
	for (var j = i + 1; j < points.length; j++) {
        var distance = getDistance(parseInt(points[i].split(' ')[0]), parseInt(points[i].split(' ')[1]), parseInt(points[j].split(' ')[0]), parseInt(points[j].split(' ')[1]));
        if (smallestDistance > distance) {
			smallestDistance = distance;
			nearest = j;
		}
	}
    var newPoint = points[i+1];
	points[i + 1] = points[nearest];
	points[nearest] = newPoint;
    totalLength += smallestDistance;
}

var lastPoint = points[points.length - 1].split(' ');
var firstPoint = points[0].split(' ');

totalLength += getDistance(parseInt(lastPoint[0]),parseInt(lastPoint[1]),parseInt(firstPoint[0]),parseInt(firstPoint[1]));

print(Math.round(totalLength,0));
